mapper.select:
SELECT  FROM ZHW_MEM WHERE  TJ_MAN=#{tjMan}  
	and  JH_STATUS = #{jhStatus}

	and HY_CODE = #{hyCode}

order by XG_TIME DESC
limit #{start},#{pageSize}

SELECT  FROM ZHW_MEM WHERE  JH_STATUS in ${jhStatus} and KT_MAN=#{currentUser}

	and HY_CODE = #{hyCode}

order by XG_TIME DESC
 limit #{start},#{pageSize}

SELECT COUNT(1) FROM ZHW_MEM WHERE TJ_MAN = #{tjMan}
	and  JH_STATUS = #{jhStatus}

	and HY_CODE = #{hyCode}

